    private void MenuMhsMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MenuMhsMouseClicked
        if(dm==null || dm.isClosed() ){
            dm = new DataMhs();
            dskutama.add(dm);
            dm.setLocation(this.getWidth()/2 - dm.getWidth()/2, this.getHeight()/2-dm.getHeight()/2);
            dm.setVisible(true);
            dd.dispose();
            dmatkul.dispose();
        }else{
            dm.setVisible(true);
        }
    }//GEN-LAST:event_MenuMhsMouseClicked

