    private void MenuDosenMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MenuDosenMouseClicked
        if(dd==null || dd.isClosed() ){
            dd = new DataDosen();
            dskutama.add(dd);
            dd.setLocation(this.getWidth()/2 - dd.getWidth()/2, this.getHeight()/2-dd.getHeight()/2);
            dd.setVisible(true);
            dm.dispose();
            dmatkul.dispose();
        }else{
            dd.setVisible(true);
        }
    }//GEN-LAST:event_MenuDosenMouseClicked

    private void MenuMatkulMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MenuMatkulMouseClicked
        if(dmatkul==null || dmatkul.isClosed() ){
            dmatkul = new DataMatkul();
            dskutama.add(dmatkul);
            dmatkul.setLocation(this.getWidth()/2 - dmatkul.getWidth()/2, this.getHeight()/2-dmatkul.getHeight()/2);
            dmatkul.setVisible(true);
            dm.dispose();
            dd.dispose();
        }else{
            dmatkul.setVisible(true);
        }
    }//GEN-LAST:event_MenuMatkulMouseClicked

